$(document).ready(function(){
    for (var  i=0;i<$(".mdl-card").length;i++){
        $(".mdl-card").css("height",$(".mdl-card").width()*0.65+"px");
    }
    $(window).resize(function(){
        for (var  i=0;i<$(".mdl-card").length;i++){
            $(".mdl-card").css("height",$(".mdl-card").width()*0.65+"px");
        }
    });
});

